// breakpoints.xs = breakpoints[0];
// breakpoints.s = breakpoints[1];
// breakpoints.m = breakpoints[2];
// breakpoints.l = breakpoints[3];

// Visibility values to hide/show elements in diferent viewport sizes
// export const vs = {
//   xs: ['flex', "none"],
//   s: ["none", 'flex', "none"],
//   m: ["none", "none", 'flex', "none"],
//   l: ["none", "none", "none", 'flex'],
// };
export const vs = (visible = "flex", size = "xs") => {
  switch (size) {
    case "xs":
      return [visible, "none"];
    case "s":
      return ["none", visible, "none"];
    case "m":
      return ["none", "none", visible, "none"];
    case "l":
      return ["none", "none", "none", visible];
    default:
      return [visible, "none"];
  }
};
